#include <iostream>
#include <string>
#include <vector>
#include <sstream>
#include <algorithm>

std::string markdownToHtml(const std::string& markdown) {
    std::string html;
    std::istringstream iss(markdown);
    std::string line;

    while (std::getline(iss, line)) {
        // リストの処理
        if (line.front() == '-' || line.front() == '*') {
            html += "<ul>\n";
            std::istringstream listLine(line);
            std::string listItem;
            while (std::getline(listLine, listItem, '\n')) {
                if (!listItem.empty()) {
                    html += "  <li>" + listItem.substr(1) + "</li>\n";
                }
            }
            html += "</ul>\n";
        } else if (line.front() == '>') {
            // ブロック引用の処理
            html += "<blockquote>\n" + line.substr(1) + "\n</blockquote>\n";
        } else {
            // テキストの強調表示とハイライトの処理
            std::string::size_type pos = 0;
            while ((pos = line.find("'''", pos)) != std::string::npos) {
                std::string highlighted = line.substr(pos, 3);
                html += "<span class='highlight'>" + highlighted + "</span>";
                pos += 3;
            }
            while ((pos = line.find("``", pos)) != std::string::npos) {
                std::string emphasized = line.substr(pos, 2);
                html += "<span class='emphasize'>" + emphasized + "</span>";
                pos += 2;
            }
            html += line;
        }
    }

    return html;
}

