int dirX = 1;
int dirY = 0;
int foodX = 10;
int foodY = 10;

bool gameOver = false;

// SDLの初期化
bool init() {
    if (SDL_Init(SDL_INIT_VIDEO) < 0) {
        printf("SDLの初期化に失敗しました: %s\n", SDL_GetError());
        return false;
    }

    window = SDL_CreateWindow("スネークゲーム", SDL_WINDOWPOS_UNDEFINED, SDL_WINDOWPOS_UNDEFINED,
