        return false;
    }

    renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
    if (renderer == NULL) {
        printf("レンダラーの作成に失敗しました: %s\n", SDL_GetError());
        return false;
    }

    return true;
}

// 画像の読み込み
SDL_Texture* loadTexture(const char* path) {
    SDL_Surface* surface = SDL_LoadBMP(path);
    if (surface == NULL) {
        printf("画像の読み込みに失敗しました: %s\n", SDL_GetError());
        return NULL;
    }
    SDL_Texture* texture = SDL_CreateTextureFromSurface(renderer, surface);
    SDL_FreeSurface(surface);
    return texture;
}

// リソースのロード
bool loadMedia() {
    snakeHeadTexture = loadTexture("snake_head.bmp");
    snakeBodyTexture = loadTexture("snake_body.bmp");
    foodTexture = loadTexture("food.bmp");
    return snakeHeadTexture != NULL && snakeBodyTexture != NULL && foodTexture != NULL;
}

// リソースの解放
void closeSDL() {
    if (foodTexture) SDL_DestroyTexture(foodTexture);
    if (snakeBodyTexture) SDL_DestroyTexture(snakeBodyTexture);
    if (snakeHeadTexture) SDL_DestroyTexture(snakeHeadTexture);
    if (renderer) SDL_DestroyRenderer(renderer);
    if (window) SDL_DestroyWindow(window);

    SDL_Quit();
}

// スネークの初期化
void initSnake() {
    for (int i = 0; i < snakeLength; i++) {
        snake[i].x = 10 - i;
        snake[i].y = 10;
    }
}

// ランダムな餌の生成
void generateFood() {
  foodX = rand() % (SCREEN_WIDTH / CELL_SIZE);
  foodY = rand() % (SCREEN_HEIGHT / CELL_SIZE);
}

// スネークの移動
void moveSnake() {
    for (int i = snakeLength - 1; i > 0; i--) {
        snake[i].x = snake[i - 1].x;
        snake[i].y = snake[i - 1].y;
    }
    snake[0].x += dirX;
    snake[0].y += dirY;

    if (snake[0].x < 0 || snake[0].x >= SCREEN_WIDTH / CELL_SIZE || snake[0].y < 0 || snake[0].y >= SCREEN_HEIGHT / CELL_SIZE) {
       gameOver = true;
    }

     // 自己衝突の判定
    for (int i = 1; i < snakeLength; i++) {
        if (snake[0].x == snake[i].x && snake[0].y == snake[i].y) {
            gameOver = true;
            break;
        }
    }
}

// 餌を食べたか判定
void checkFoodCollision() {
  if (snake[0].x == foodX && snake[0].y == foodY) {
    snakeLength++;
    generateFood();
  }
}

// スネークを描画
void drawSnake() {
  SDL_Rect rect;
  for (int i = 0; i < snakeLength; i++) {
      rect.x = snake[i].x * CELL_SIZE;
      rect.y = snake[i].y * CELL_SIZE;
      rect.w = CELL_SIZE;
      rect.h = CELL_SIZE;
      if(i == 0)
          SDL_RenderCopy(renderer, snakeHeadTexture, NULL, &rect);
      else
          SDL_RenderCopy(renderer, snakeBodyTexture, NULL, &rect);
  }
}

// 餌を描画
void drawFood() {
  SDL_Rect rect;
  rect.x = foodX * CELL_SIZE;
  rect.y = foodY * CELL_SIZE;
  rect.w = CELL_SIZE;
  rect.h = CELL_SIZE;
  SDL_RenderCopy(renderer, foodTexture, NULL, &rect);
}

// ゲームループ
void gameLoop() {
  SDL_Event event;
  bool quit = false;

  while (!quit) {
        while(SDL_PollEvent(&event)){
            if(event.type == SDL_QUIT){
                quit = true;
            }
            if(event.type == SDL_KEYDOWN){
                switch(event.key.keysym.sym){
                  case SDLK_UP:
                    if(dirY != 1){
                      dirX = 0;
                      dirY = -1;
                    }
                    break;
                  case SDLK_DOWN:
                     if(dirY != -1){
                      dirX = 0;
                      dirY = 1;
                    }
                    break;
                  case SDLK_LEFT:
                    if(dirX != 1){
                      dirX = -1;
                      dirY = 0;
                    }
                    break;
                  case SDLK_RIGHT:
                    if(dirX != -1){
                      dirX = 1;
                      dirY = 0;
                    }
                    break;
                }
            }
        }
        if(gameOver){
          printf("ゲームオーバー\n");
          break;
        }

        moveSnake();
        checkFoodCollision();


        SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
        SDL_RenderClear(renderer);
        drawSnake();
        drawFood();
        SDL_RenderPresent(renderer);

        SDL_Delay(100); // フレームレート調整
    }
}


int main(int argc, char* argv[]) {
  srand(time(NULL));
  if (!init()) {
    return 1;
  }
  if(!loadMedia()){
    closeSDL();
    return 1;
  }
  initSnake();
  generateFood();

  gameLoop();
  closeSDL();

  return 0;
}
