    Point* body;
    int length;
    int dir;
} Snake;

void initSnake(Snake* snake) {
    snake->length = 1;
    snake->body = (Point*)malloc(sizeof(Point));
    snake->body[0].x = WINDOW_WIDTH / 2;
    snake->body[0].y = WINDOW_HEIGHT / 2;
    snake->dir = 0; // 0:右, 1:下, 2:左, 3:上
}

void updateSnake(Snake* snake) {
    // スネークの移動ロジック
    // ここにスネークの位置を更新する処理を追加
}

void render(SDL_Renderer* renderer, Snake* snake) {
    SDL_SetRenderDrawColor(renderer, 0, 255, 0, 255);
    for (int i = 0; i < snake->length; i++) {
        SDL_Rect rect = { snake->body[i].x, snake->body[i].y, SNAKE_SIZE, SNAKE_SIZE };
        SDL_RenderFillRect(renderer, &rect);
    }
}

int main(int argc, char* argv[]) {
    SDL_Init(SDL_INIT_VIDEO);
    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_UNDEFINED, SDL_WINDOWPOS_UNDEFINED, WINDOW_WIDTH, WINDOW_HEIGHT, 0);
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);

