    std::ifstream file(filename); // ファイルを開く
    if (!file) {
        std::cerr << "エラー: ファイルを開くことができませんでした。" << std::endl;
        return 1;
    }

    std::string line;
    int count = 0; // 単語のカウント

    // 行ごとにファイルを読み込む
    while (std::getline(file, line)) {
        // 行中の単語の出現回数をカウント
        size_t pos = line.find(targetWord);
        while (pos != std::string::npos) {
            count++;
            pos = line.find(targetWord, pos + targetWord.length());
        }
    }

    file.close(); // ファイルを閉じる

    // 結果を表示
    std::cout << "単語 '" << targetWord << "' の出現回数: " << count << std::endl;

    return 0;
}
